% TGexpf_pk.m: calculate the tonic gain needed by the NI% calculated to match pulses generated by PHfn_pk.m  % Written by:  Jonathan Jacobs%              October 1998  (last mod: 10/25/98)function out = efun( in )sgn = sign(in);in = abs(in);c(1)=-0.4226938702;c(2)=5.227160426; lambda(1)=0.07041410249;lambda(2)=0.004767145058;numcoeff = length(lambda);out=0;for i = 1:numcoeff   out =  out + c(i)*exp(-lambda(i)*in);endout = sgn*out;