figuret=maket(rh);plot(t,rh,'c',t,lh,'c--',t,rv,'y',t,lv,'y--',t,hh,'r',t,hv,'r--')maxpt=max(max([lh,rh,lv,rv,hh,hv]));minpt=min(min([lh,rh,lv,rv,hh,hv]));maxt=max(t);rehH=text(maxt,maxpt,'REH');lehH=text(maxt,maxpt-(maxpt-minpt)*.2,'LEH');revH=text(maxt,maxpt-(maxpt-minpt)*.4,'REV');levH=text(maxt,maxpt-(maxpt-minpt)*.6,'LEV');hhH=text(maxt,maxpt-(maxpt-minpt)*.8,'HH');hvH=text(maxt,minpt,'HV');set(rehH,'color','c');set(lehH,'color','c');set(revH,'color','y');set(levH,'color','y');set(hhH,'color','r');set(hvH,'color','r');if exist('namelist','var'), title(nameclean(namelist)); endeptdragger