%la.m:  labels, nothing but labels.%       collect them, trade them, swap them with your friends.  Be the%       first on your block to have the whole collection.function la(lstr)orig = lstr;lstr = lower(lstr);if strcmp(lstr, 'rehor')   gtext('REH') elseif strcmp(lstr, 'lehor')   gtext('LEH') elseif strcmp(lstr, 'revrt')   gtext('REV') elseif strcmp(lstr, 'levrt')   gtext('LEV') elseif strcmp(lstr, 'retor')   gtext('RET') elseif strcmp(lstr, 'letor')   gtext('LET') elseif strcmp(lstr, 'hdhor')   gtext('HH') elseif strcmp(lstr, 'hdvrt')   gtext('HV') elseif strcmp(lstr, 'stim')   gtext('STM') elseif strcmp(lstr, 'ris')   gtext('RIS') elseif strcmp(lstr, 'hor')   gtext('Horizontal') elseif strcmp(lstr, 'vrt')   gtext('Vertical') elseif strcmp(lstr, 'hosop')   gtext('Horizontal Oscillopsia') elseif strcmp(lstr, 'vosop')   gtext('Vertical Oscillopsia') elseif strcmp(lstr, 'refx')   gtext('RE Fixation') elseif strcmp(lstr, 'lefx')   gtext('LE Fixation') elseif strcmp(lstr, 'bevw')   gtext('BE Viewing') elseif strcmp(lstr, 'befx')   gtext('BE Fixation') elseif strcmp(lstr, 'xtrmv')   gtext('Crosstalk Removed') elseif strcmp(lstr, 'altfx')   gtext('Alternate Fixation') elseif strcmp(lstr, 'rcov')   gtext('Reverse Cover Test') elseif strcmp(lstr, 'altoc')   gtext('Alternate Occlusion') elseif strcmp(lstr, 'altcov')   gtext('Alternate Cover') elseif strcmp(lstr, 'reoc')   gtext('RE Occlusion') elseif strcmp(lstr, 'leoc')   gtext('LE Occlusion') elseif strcmp(lstr, 'odfx')   gtext('OD Fixation') elseif strcmp(lstr, 'osfx')   gtext('OS Fixation') elseif strcmp(lstr, 'ouvw')   gtext('OU Viewing') elseif strcmp(lstr, 'oufx')   gtext('OU Fixation')  elseif strcmp(lstr, 'acnt')   gtext('Area Centralis')elseif strcmp(lstr, 'repos')   gtext('RE Position') elseif strcmp(lstr, 'lepos')   gtext('LE Position') elseif strcmp(lstr, 'conv')   gtext('Convergence') elseif strcmp(lstr, 'vor')   gtext('VOR') elseif strcmp(lstr, 'vvor')   gtext('VVOR') elseif strcmp(lstr, 'sacc')   gtext('Saccades') elseif strcmp(lstr, 'pur')   gtext('Pursuit') elseif strcmp(lstr, 'lcal')   gtext('Linear-CAL') elseif strcmp(lstr, 'xcal')   gtext('Extended-CAL') elseif strcmp(lstr, 'rcal')   gtext('ArcSin-CAL') elseif strcmp(lstr, 'obrc')   gtext('Ober Rough-CAL') %% nice doggy %%  elseif strcmp(lstr, 'ambs')   gtext('Achiasmatic Mutant Belgian Sheepdog') elseif strcmp(lstr, 'mik')   gtext('Mike') elseif strcmp(lstr, 'mil')   gtext('Milton') elseif strcmp(lstr, 'msh')   gtext('Misha') elseif strcmp(lstr, 'mik')   gtext('Mike') elseif strcmp(lstr, 'mly')   gtext('Molly') elseif strcmp(lstr, 'prs')   gtext('Priscilla') elseif strcmp(lstr, 'hetro')   gtext('Heterozygote Belgian Sheepdog') elseif strcmp(lstr, 'hla')   gtext('Halla') elseif strcmp(lstr, 'hna')   gtext('Hanna') elseif strcmp(lstr, 'hns')   gtext('Hans') elseif strcmp(lstr, 'hea')   gtext('Heather') elseif strcmp(lstr, 'hta')   gtext('Henrietta') elseif strcmp(lstr, 'hra')   gtext('Hera') elseif strcmp(lstr, 'hly')   gtext('Holly') elseif strcmp(lstr, 'nrml')   gtext('Normal') elseif strcmp(lstr, 'dog')   gtext('Dog') elseif strcmp(lstr, 'bdw')   gtext('Brendjwyn') elseif strcmp(lstr, 'bdy')   gtext('Brandy') elseif strcmp(lstr, 'cpr')   gtext('Copper') elseif strcmp(lstr, 'dyl')   gtext('Dylan') elseif strcmp(lstr, 'elv')   gtext('Elvira') elseif strcmp(lstr, 'lcy')   gtext('Lucy') elseif strcmp(lstr, 'pea')   gtext('Peanut') elseif strcmp(lstr, 'rdg')   gtext('Red Dog') elseif strcmp(lstr, 'rng')   gtext('Ringo') elseif strcmp(lstr, 'chr')   gtext('Charlie')  elseif strcmp(lstr, 'max')   gtext('Max')elseif strcmp(lstr, 'husk')   gtext('Siberian Husky Crossbreed') else   %gtext(orig)   error('la.m: unknown argument')end