plthFigH = figure;plthFigColor = get(plthFigH,'color');if plthFigColor(1) == 0.8   lhColor = 'g';   rhColor = 'b'; elseif plthFigColor(1) == 0;   lhColor = 'y';   rhColor = 'c';endif ~isempty(rh)   t=maket(rh); elseif ~isempty(lh)   t=maket(lh); else   disp('No horizontal data!?!')   returnend plot(t,rh,rhColor,t,lh,lhColor)maxpt=max(max([lh,rh]));minpt=min(min([lh,rh]));maxt=max(t);rehH=text(maxt,maxpt,'REH');lehH=text(maxt,minpt,'LEH');set(rehH,'color', rhColor);set(lehH,'color', lhColor);if exist('namelist','var'), title(nameclean(namelist)); endeptdraggerclear reH leH plthFigH plthFigColor lhColor