tempFigH = figure;tempFigColor = get(tempFigH,'color');if tempFigColor(1) == 0.8   lhColor = 'g'; elseif tempFigColor(1) == 0;   lhColor = 'y';end if ~isempty(rv)   t=maket(rv); elseif ~isempty(lv)   t=maket(lv); else   disp('No vertical data!?!')   returnend%plot(t,rh,'c',t,lh,'y',t,rv,'c--',t,lv,'y--')plot(t,rh,'c',t,lh,lhColor)hold onplot(t,rv,'b-')plot(t,lv,'color',[.7 .5 0],'linestyle','-')%plot(t,lv,'color',[.7 .5 0],'linestyle','--')maxpt=max(max([lh,rh,lv,rv]));minpt=min(min([lh,rh,lv,rv]));maxt=max(t);rehH=text(maxt,maxpt,'REH');lehH=text(maxt,maxpt-(maxpt-minpt)*.33,'LEH');revH=text(maxt,maxpt-(maxpt-minpt)*.66,'REV');levH=text(maxt,minpt,'LEV');set(rehH,'color','c');set(lehH,'color',lhColor);set(revH,'color','b');set(levH,'color',[.7 .5 0]);if exist('namelist','var'), title(nameclean(namelist)); endeptdraggerclear rehH lehH revH levHclear tempFigH tempFigColor lhColor