figuret=maket(rh);plot(t,rh,'c',t,lh,'c--',t,rv,'y',t,lv,'y--',t,st,'r',t,stv,'r--')maxpt=max(max([lh,rh,st,lv,rv,stv]));minpt=min(min([lh,rh,st,lv,rv,stv]));maxt=max(t);rehH=text(maxt,maxpt,'REH');lehH=text(maxt,maxpt-(maxpt-minpt)*.2,'LEH');revH=text(maxt,maxpt-(maxpt-minpt)*.4,'REV');levH=text(maxt,minpt,-(maxpt-minpt)*.6,'LEV');stm=text(maxt,maxpt-(maxpt-minpt)*.8,'HSTIM');stmv=text(maxt,minpt,'VSTIM');set(rehH,'color','c');set(stm,'color','r');set(lehH,'color','c');set(revH,'color','y');set(stmv,'color','r');set(levH,'color','y');title(nameclean(namelist))eptdragger