tempFigH = figure;tempFigColor = get(tempFigH,'color');if tempFigColor(1) == 0.8   lhColor = 'g'; elseif tempFigColor(1) == 0;   lhColor = 'y';end if ~isempty(rt)   t=maket(rt); elseif ~isempty(lt)   t=maket(lt); else   disp('No torsional data!?!')   returnendfiguret=maket(rh);plot(t,rh,'c',t,lh,'c--',t,rv,'y',t,lv,'y--',t,rt,'g',t,lt,'g--')maxpt=max(max([lh,rh,lv,rv,lt,rt]));minpt=min(min([lh,rh,lv,rv,lt,rt]));maxt=max(t);rehH=text(maxt,maxpt,'REH');lehH=text(maxt,maxpt-(maxpt-minpt)*.2,'LEH');revH=text(maxt,maxpt-(maxpt-minpt)*.4,'REV');levH=text(maxt,maxpt-(maxpt-minpt)*.6,'LEV');retH=text(maxt,maxpt-(maxpt-minpt)*.8,'RET');letH=text(maxt,minpt,'LET');set(rehH,'color','c');set(lehH,'color','c');set(revH,'color','y');set(levH,'color','y');set(retH,'color','g');set(letH,'color','g');title(nameclean(namelist))eptdraggerclear rehH lehH revH levH retH letHclear tempFigH tempFigColor lhColor