clear clear globalshowGraphs = 0;rdt=(1/samp_freq:1/samp_freq:length(lh)/samp_freq);[nmRows, nmCols] = size(namearray);nameStr = namearray(nmRows, :);if (1)   horH = figure;   plot(t, lh, 'y--', t, rh, 'c')   title([nameStr ' horizontal'])endzoomtoolif (1)   vertH = figure;   plot(t, lv, 'y--', t, rv, 'c')   title([nameStr ' vertical'])endif (1)   leftH = figure;   plot(t, lh, 'y', t, lv, 'c--')   title([nameStr ' Left eye (lh = solid, lv = dashed)'])endif (1)   rightH = figure;   plot(t, rh, 'c', t, rv, 'y--')   title([nameStr ' Right eye (rh = solid, rv = dashed)'])endclear  nmRows nmCols vertH horH leftH rightH