function cursHandle = crsrmake(ax,name,dir,posit,ltype,lcolr, hCurMode)%CRSRMAKE Create axes crsr.%       H=CRSRMAKE(AXES,'NAME','DIRECTION',POSITION,'LINETYPE',LINECOLOR)%       creates a cursor on the axes pointed to by the handle%       axes. If not handle is provided, the current axes is%       used. 'NAME' is a unique identifier for the cursor and%       is used to reference the cursor during subsequent cursor%       operations. 'direction' can be either 'horizontal' or%       'vertical'. POSITION is a scalar number of where the%       cursor is to be located (x-axis location for 'vertical'%       cursors, y-axis location for 'horizontal' cursors).%       'LINETYPE' is any valid MATLAB linetype. An error occurs%       if a cursor is created beyond the limits of the axes.%%       See also CRSRMOVE, CRSRDEL, CRSRON, CRSROFF%       Dennis W. Brown 1-10-94%       Copyright (c) 1994 by Dennis W. Brown%       May be freely distributed.%       Not for use in commercial products.%	p skelly added lcolr variable to function inputs to specify%	the cursor line color in rgb vector format% Modifications by Jonathan Jacobs%                  September 1997 - June 2002 (last mod: 06/08/02)% get current axis datax = get(ax,'XLim');y = get(ax,'YLim');val = get(hCurMode,'value');str = get(hCurMode,'string');emode = str(val,:); % draw the crsrif strcmp(dir,'vertical'),    % check the location    if posit < x(1) | posit > x(2)                %error('crsrmake: Cursor location out of axes range...');    end;    % actually draw the damn thing    axes(ax);    cursHandle = line('Erasemode',emode,'LineStyle',ltype,...    	'Color', lcolr,...    	'HitTest','off',...        'Xdata',[posit posit],'Ydata',y,...        'Visible','on','Userdata',name);elseif strcmp(dir,'horizontal'),    % check the location    if posit < y(1) | posit > y(2)                %error('crsrmake: Cursor location out of axes range...');    end;    % actually draw the damn thing    % setting 'HitTest' to 'off' prevents the horizontal cursor    % from intercepting a click meant to be delivered to the data    % line.  Without this command, it is impossible to make the     % h/v cursor pair move to a point on the 'zoomed' data line that    % is covered by the h cursor.  Clever, eh?    axes(ax);    cursHandle = line('Erasemode',emode,'LineStyle',ltype,...    	'Color',lcolr,...    	'HitTest','off',...        'Xdata',x,'Ydata',[posit posit],...        'Visible','on','Userdata',name);else    error('crsrmake: Invalid cursor direction...');end;