function plth(from,to)global rh lh st datanameif ~exist('from','var'), from = 1; endif ~exist('to','var')   if ~isempty(rh)      to = length(rh);   elseif ~isempty(lh)      to = length(lh);   else      keyboard   endendplthFigH = figure; box ONplthFigColor = get(plthFigH,'color');if plthFigColor(1) >= 0.6     %% Light background   lhColor = 'g';   rhColor = 'b'; elseif plthFigColor(1) < 0.6   lhColor = 'y';   rhColor = 'c';endif ~isempty(rh)   t=maket(rh(from:to)); elseif ~isempty(lh)   t=maket(lh(from:to)); else   disp('No horizontal data!?!')   returnendmaxt=max(t);fontsize=12;hold onif ~isempty(lh)   lhH=plot(t,lh(from:to),lhColor);   lhH.DisplayName='lh';   lhtxtH=text(maxt,lh(to),' LH');   lhtxtH.Color=lhColor;   lhtxtH.FontSize=fontsize;endif ~isempty(rh)   rhH=plot(t,rh(from:to),rhColor);   rhH.DisplayName='rh';   rhtxtH=text(maxt,rh(to),' RH');   rhtxtH.Color=rhColor;   rhtxtH.FontSize=fontsize;endif ~isempty(st)   stH=plot(t,st(from:to),'r');   stH.DisplayName='st';   sttxtH=text(maxt,st(to),' HStm');   sttxtH.Color='r';   sttxtH.FontSize=fontsize;endif exist('dataname','var')   if ~isempty(dataname)      title(nameclean(dataname));   endendept;