function plth_vel(from,to)global rh lh st datanameif ~exist('from','var'), from = 1; endif ~exist('to','var')   if ~isempty(rh)      to = length(rh);   elseif ~isempty(lh)      to = length(lh);   else      keyboard   endendrh_vel = d2pt(rh,2);lh_vel = d2pt(lh,2);plthFigH = figure; box ONplthFigColor = get(plthFigH,'color');if plthFigColor(1) >= 0.6     %% Light background   lhColor = 'g';   rhColor = 'b'; elseif plthFigColor(1) < 0.6   lhColor = 'y';   rhColor = 'c';endif ~isempty(rh_vel)   t=maket(rh_vel(from:to)); elseif ~isempty(lh_vel)   t=maket(lh_vel(from:to)); else   disp('No horizontal data!?!')   returnendmaxt=max(t);fontsize=12;hold onif ~isempty(lh_vel)   lhH=plot(t,lh_vel(from:to),lhColor);   lhH.DisplayName='lh_vel';   lhtxtH=text(maxt,lh_vel(to),' LH_vel');   lhtxtH.Color=lhColor;   lhtxtH.FontSize=fontsize;endif ~isempty(rh_vel)   rhH=plot(t,rh_vel(from:to),rhColor);   rhH.DisplayName='rh_vel';   rhtxtH=text(maxt,rh_vel(to),' RH_vel');   rhtxtH.Color=rhColor;   rhtxtH.FontSize=fontsize;endif ~isempty(st)   stH=plot(t,st(from:to),'r');   stH.DisplayName='st';   sttxtH=text(maxt,st(to),' HStm');   sttxtH.Color='r';   sttxtH.FontSize=fontsize;endif exist('dataname','var')   if ~isempty(dataname)      title(nameclean(dataname));   endendevt;