function plthv(from,to)global rh rv lh lv st sv datanameif ~exist('from','var'), from = 1; endif ~exist('to','var')   if ~isempty(rh)      to = length(rh);   elseif ~isempty(lh)      to = length(lh);   else      keyboard   endendplthFigH = figure; box ONplthFigColor = get(plthFigH,'color');if plthFigColor(1) >= 0.6     %% Light background   lhColor = 'g';   rhColor = 'b'; elseif plthFigColor(1) < 0.6   lhColor = 'y';   rhColor = 'c';end%lvColor = [.7 .5 0];lvColor = lhColor;rvColor = rhColor;fontsize=12;novrt=0; %#ok<NASGU>if ~isempty(rv)   t=maket(rv(from:to)); elseif ~isempty(lv)   t=maket(lv(from:to)); else   disp('No vertical data!?!')   plth   returnendmaxt=max(t);hold onif ~isempty(lh)   lhH=plot(t,lh(from:to),lhColor);   lhH.DisplayName='lh';   lhtxtH=text(maxt,lh(to),' LH');   lhtxtH.Color=lhColor;   lhtxtH.FontSize=fontsize;end   if ~isempty(rh)   rhH=plot(t,rh(from:to),rhColor);   rhH.DisplayName='rh';   rhtxtH=text(maxt,rh(to),' RH');   rhtxtH.Color=rhColor;   rhtxtH.FontSize=fontsize;end   if ~isempty(st)   stH=plot(t,st(from:to),'r');   stH.DisplayName='st';   sttxtH=text(maxt,st(to),' HStm');   sttxtH.FontSize=fontsize;   sttxtH.Color='r';end  if ~isempty(lv)   lvH=plot(t,lv(from:to),lvColor);   lvH.DisplayName='lv';   lvtxtH=text(maxt,lv(to),' LV');   lvtxtH.Color=lvColor;   lvtxtH.FontSize=fontsize;end   if ~isempty(rv)   rvH=plot(t,rv(from:to),rvColor);   rvH.DisplayName='rv';   rvtxtH=text(maxt,rv(to),' RV');   rvtxtH.Color=rvColor;   rvtxtH.FontSize=fontsize;end   if ~isempty(sv)   svH=plot(t,sv(from:to),'r');   svH.DisplayName='sv';   svtxtH=text(maxt,sv(to),' VStm');   svtxtH.Color='r';   svtxtH.FontSize=fontsize;end   if exist('dataname','var')   if ~isempty(dataname)      title(nameclean(dataname));   endend   ept;