function plthv_velglobal rh rv lh lv st sv datanameif ~exist('from','var'), from = 1; endif ~exist('to','var')   if ~isempty(rh)      to = length(rh);   elseif ~isempty(lh)      to = length(lh);   else      keyboard   endendrh_vel = d2pt(rh,2);rv_vel = d2pt(rv,2);lh_vel = d2pt(lh,2);lv_vel = d2pt(lv,2);plthFigH = figure; box ONplthFigColor = get(plthFigH,'color');if plthFigColor(1) >= 0.6     %% Light background   lhColor = 'g';   rhColor = 'b'; elseif plthFigColor(1) < 0.6   lhColor = 'y';   rhColor = 'c';end%lvColor = [.7 .5 0];lvColor = lhColor;rvColor = rhColor;fontsize=12;novrt=0; %#ok<NASGU>if ~isempty(rv_vel)   t=maket(rv_vel(from:to)); elseif ~isempty(lv_vel)   t=maket(lv_vel(from:to)); else   disp('No vertical data!?!')   plth   returnendmaxt=max(t);hold onif ~isempty(lh_vel)   lhH=plot(t,lh_vel(from:to),lhColor);   lhH.DisplayName='lh_vel';   lhtxtH=text(maxt,lh_vel(end),' LH_vel');   lhtxtH.Color=lhColor;   lhtxtH.FontSize=fontsize;end   if ~isempty(rh_vel)   rhH=plot(t,rh_vel(from:to),rhColor);   rhH.DisplayName='rh_vel';   rhtxtH=text(maxt,rh_vel(end),' RH_vel');   rhtxtH.Color=rhColor;   rhtxtH.FontSize=fontsize;end   if ~isempty(st)   stH=plot(t,st(from:to),'r');   stH.DisplayName='st';   sttxtH=text(maxt,st(to),' HStm');   sttxtH.FontSize=fontsize;   sttxtH.Color='r';end  if ~isempty(lv_vel)   lvH=plot(t,lv_vel(from:to),lvColor);   lvH.DisplayName='lv_vel';   lvtxtH=text(maxt,lv_vel(to),' LV_vel');   lvtxtH.Color=lvColor;   lvtxtH.FontSize=fontsize;end   if ~isempty(rv_vel)   rvH=plot(t,rv_vel(from:to),rvColor);   rvH.DisplayName='rv_vel';   rvtxtH=text(maxt,rv_vel(to),' RV_vel');   rvtxtH.Color=rvColor;   rvtxtH.FontSize=fontsize;end   if ~isempty(sv)   svH=plot(t,sv(from:to),'r');   svH.DisplayName='sv';   svtxtH=text(maxt,sv(to),' VStm');   svtxtH.Color='r';   svtxtH.FontSize=fontsize;end   if exist('dataname','var')   if ~isempty(dataname)      title(nameclean(dataname));   endend   evt;