figuret=maket(rh);plot(t,rh,'c',t,lh,'c--',t,rv,'y',t,lv,'y--',t,rt,'g',t,lt,'g--',t,hh,'r',t,hv,'--')maxpt=max(max([lh,rh,lv,rv,lt,rt,hh,hv]));minpt=min(min([lh,rh,lv,rv,lt,rt,hh,hv]));maxt=max(t);rehH=text(maxt,maxpt,'REH');lehH=text(maxt,maxpt-(maxpt-minpt)*.14,'LEH');revH=text(maxt,maxpt-(maxpt-minpt)*.28,'REV');levH=text(maxt,maxpt-(maxpt-minpt)*.42,'LEV');retH=text(maxt,maxpt-(maxpt-minpt)*.56,'RET');letH=text(maxt,minpt-(maxpt-minpt)*.7,'LET');hhH=text(maxt,minpt-(maxpt-minpt)*.84,'HH');hvH=text(maxt,minpt,'HV');set(rehH,'color','c');set(lehH,'color','c');set(revH,'color','y');set(levH,'color','y');set(retH,'color','g');set(letH,'color','g');set(hhH,'color','r');set(hvH,'color','r');title(nameclean(namelist))eptdragger