t=maket(lt);figure;subplot(2,1,1)plot(t,rt,'c')maxpt=max(max(rt));minpt=min(min(rt));maxt=max(t);rehH=text(maxt,maxpt,'RET');set(rehT,'color','c');ylabel('Eye Position (deg)')title(nameclean(namelist))subplot(2,1,2)plot(t,lt,'y--')maxpt=max(max(lt));minpt=min(min(lt));lehH=text(maxt,maxpt,'LET');set(lehT,'color','y');ylabel('Eye Position (deg)')xlabel('Time (sec)')orient landscape