function pltv(from,to)global rv lv sv datanameif ~exist('from','var'), from = 1; endif ~exist('to','var')   if ~isempty(rv)      to = length(rv);   elseif ~isempty(lv)      to = length(lv);   else      keyboard   endendplthFigH = figure; box ONplthFigColor = get(plthFigH,'color');if plthFigColor(1) >= 0.6     %% Light background   lvColor = 'g';   rvColor = 'b'; elseif plthFigColor(1) < 0.6   lvColor = 'y';   rvColor = 'c';endif ~isempty(rv)   t=maket(rv(from:to)); elseif ~isempty(lv)   t=maket(lv(from:to)); else   disp('No vertical data!?!')   returnendmaxt=max(t);fontsize=12;hold onif ~isempty(lv)   lvH=plot(t,lv(from:to),lvColor);   lvH.DisplayName='lv';   lvtxtH=text(maxt,lv(to),' LV');   lvtxtH.Color=lvColor;   lvtxtH.FontSize=fontsize;endif ~isempty(rv)   rvH=plot(t,rv(from:to),rvColor);   rvH.DisplayName='rv';   rvtxtH=text(maxt,rv(to),'RV');   rvtxtH.Color=rvColor;   rvtxtH.FontSize=fontsize;endif ~isempty(sv)   svH=plot(t,sv(from:to),'r');   svH.DisplayName='sv';   svtxtH=text(maxt,sv(to),' VStm');   svtxtH.Color='r';   svtxtH.FontSize=fontsize;endif exist('dataname','var')   if ~isempty(dataname)      title(nameclean(dataname));   endendept;