function pltv_vel(from,to)global rv lv sv datanameif ~exist('from','var'), from = 1; endif ~exist('to','var')   if ~isempty(rv)      to = length(rv);   elseif ~isempty(lv)      to = length(lv);   else      keyboard   endendrv_vel = d2pt(rv,2);lv_vel = d2pt(lv,2);plthFigH = figure; box ONplthFigColor = get(plthFigH,'color');if plthFigColor(1) >= 0.6     %% Light background   lvColor = 'g';   rvColor = 'b'; elseif plthFigColor(1) < 0.6   lvColor = 'y';   rvColor = 'c';endif ~isempty(rv_vel)   t=maket(rv_vel(from:to)); elseif ~isempty(lv_vel)   t=maket(lv_vel(from:to)); else   disp('No horizontal data!?!')   returnendmaxt=max(t);fontsize=12;hold onif ~isempty(lv_vel)   lvH=plot(t,lv_vel(from:to),lvColor);   lvH.DisplayName='lv_vel';   lvtxtH=text(maxt,lv_vel(to),' LV_vel');   lvtxtH.Color=lvColor;   lvtxtH.FontSize=fontsize;endif ~isempty(rv_vel)   rvH=plot(t,rv_vel(from:to),rvColor);   rvH.DisplayName='rv_vel';   rvtxtH=text(maxt,rv_vel(to),' RV_vel');   rvtxtH.Color=rvColor;   rvtxtH.FontSize=fontsize;endif ~isempty(sv)   svH=plot(t,sv(from:to),'r');   svH.DisplayName='sv';   svtxtH=text(maxt,sv(to),' VStm');   svtxtH.Color='r';   svtxtH.FontSize=fontsize;endif exist('dataname','var')   if ~isempty(dataname)      title(nameclean(dataname));   endendevt;